INSERT OR REPLACE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (3635, 0, 'Brigatinib', 'Grapefruit juice may increase the plasma concentrations of brigatinib.  The proposed mechanism is inhibition of CYP450 3A4-mediated first-pass metabolism in the gut wall by certain compounds present in grapefruit.  In general, the effect of grapefruit juice is concentration-, dose- and preparation-dependent, and can vary widely among brands.  Certain preparations of grapefruit juice (e.g., high dose, double strength) have sometimes demonstrated potent inhibition of CYP450 3A4, while other preparations (e.g., low dose, single strength) have typically demonstrated moderate inhibition.', 'grapefruit juice', 'Moderate', 'Brigatinib may be taken with or without food.  Patients should avoid consumption of grapefruit and grapefruit juice during treatment with brigatinib.', NULL, NULL, 'DDInter', 0);
