INSERT OR REPLACE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (3657, 1780, 'Budesonide', 'Grapefruit juice may increase the plasma concentrations and systemic effects of orally administered budesonide.  The proposed mechanism is inhibition of CYP450 3A4-mediated first-pass metabolism in the gut wall by certain compounds present in grapefruits.  According to the manufacturer, the systemic exposure of oral budesonide approximately doubles after extensive intake of grapefruit juice.', 'grapefruit juice', 'Moderate', 'Patients receiving budesonide should avoid the regular consumption of grapefruits and grapefruit juice to prevent undue increases in plasma budesonide levels and systemic effects.', NULL, NULL, 'DDInter', 0);
