INSERT OR REPLACE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (4020, 2224, 'Cefoperazone', 'Some cephalosporins may occasionally induce a disulfiram-like reaction when coadministered with alcohol.  The interaction has been reported for cefamandole, cefoperazone, cefotetan, and moxalactam.  These agents contain an N-methylthiotetrazole (NMTT) side chain that may inhibit aldehyde dehydrogenase (ALDH) similar to disulfiram.  Following ingestion of alcohol, inhibition of ALDH results in increased concentration of acetaldehyde, the accumulation of which produces an unpleasant physiologic response referred to as the ''disulfiram reaction''.  Symptoms include flushing, throbbing in head and neck, throbbing headache, respiratory difficulty, nausea, vomiting, sweating, thirst, chest pain, palpitation, dyspnea, hyperventilation, tachycardia, hypotension, syncope, weakness, vertigo, blurred vision, and confusion.  Severe reactions may result in respiratory depression, cardiovascular collapse, arrh', 'alcohol', 'Moderate', 'Patients receiving cephalosporins with the NMTT side chain should avoid the concomitant use of alcohol and alcohol-containing products.', NULL, NULL, 'DDInter', 0);
