INSERT OR REPLACE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (4070, 0, 'Ceritinib', 'Grapefruit juice may increase the plasma concentrations of ceritinib.  The proposed mechanism is inhibition of CYP450 3A4-mediated first-pass metabolism in the gut wall by certain compounds present in grapefruit.', 'grapefruit juice', 'Major', 'Patients treated with ceritinib should avoid consumption of grapefruit, grapefruit juice, and any supplement containing grapefruit extract.  Ceritinib should be administered on an empty stomach (i.e., avoid administration within 2 hours of a meal).', NULL, NULL, 'DDInter', 0);
INSERT OR REPLACE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (4071, 468, 'Albendazole', 'Food enhances the oral bioavailability of albendazole, which is rapidly converted by hepatocytes and intestinal mucosal cells into the active metabolite, albendazole sulfoxide (ABZSX), following absorption.  The proposed mechanism is stimulation of gastric acid secretion, as the absorption of albendazole is thought to be pH-dependent.  According to the product labeling, plasma concentrations of ABZSX are up to 5-fold higher on average when albendazole is administered with a fatty meal (fat content approximately 40 g) compared to administration in the fasted state.', 'high-fat foods', 'Moderate', 'To ensure maximal oral absorption, albendazole should be taken with food.  Grapefruit juice may also enhance the oral bioavailability of albendazole.', NULL, NULL, 'DDInter', 0);
