INSERT OR REPLACE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (4114, 2707, 'Cilostazol', 'Grapefruit juice may increase the plasma concentrations of cilostazol.  The proposed mechanism is inhibition of CYP450 3A4-mediated first-pass metabolism in the gut wall by certain compounds present in grapefruits.  The extent and clinical significance are unknown.  Moreover, pharmacokinetic alterations associated with interactions involving grapefruit juice are often subject to a high degree of interpatient variability.', 'grapefruit juice', 'Moderate', 'Until more information is available, the manufacturer recommends avoiding consumption of grapefruit juice during cilostazol therapy.  Orange juice is not expected to interact with cilostazol.', NULL, NULL, 'DDInter', 0);
