INSERT OR REPLACE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (4302, 13650, 'Alectinib', 'Food significantly enhances the oral bioavailability of alectinib and its major active metabolite, M4.  According to the manufacturer, a high-fat, high-calorie meal increased the combined systemic exposure (AUC) of alectinib and M4 by 3.1-fold following oral administration of a single 600 mg dose of alectinib.', 'high-fat foods', 'Moderate', 'To ensure maximal oral absorption, alectinib should be administered with food.', NULL, NULL, 'DDInter', 0);
INSERT OR REPLACE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (4303, 2416, 'Citalopram', 'Alcohol may potentiate some of the pharmacologic effects of CNS-active agents.  Use in combination may result in additive central nervous system depression and/or impairment of judgment, thinking, and psychomotor skills.', 'alcohol', 'Moderate', 'Patients receiving CNS-active agents should be warned of this interaction and advised to avoid or limit consumption of alcohol.  Ambulatory patients should be counseled to avoid hazardous activities requiring complete mental alertness and motor coordination until they know how these agents affect them, and to notify their physician if they experience excessive or prolonged CNS effects that interfere with their normal activities.', NULL, NULL, 'DDInter', 0);
