INSERT OR REPLACE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (4321, 5425, 'Cimetidine', 'Concurrent use of cimetidine and ethanol may result in increased ethanol concentrations.  The mechanism appears to be due to inhibition of gastric alcohol dehydrogenase by cimetidine, leading to increased bioavailability of the alcohol and inhibition of hepatic metabolism of alcohol.', 'alcohol', 'Minor', 'The clinical significance of this interaction is limited.  More importantly, patients requiring cimetidine for gastrointestinal disease should be counseled to avoid alcohol to prevent worsening of their disease.  The other H-2 receptor antagonists appear to have minimal effects on the concentrations of alcohol.', NULL, NULL, 'DDInter', 0);
INSERT OR REPLACE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (4322, 2696, 'Clarithromycin', 'Grapefruit juice may delay the gastrointestinal absorption of clarithromycin but does not appear to affect the overall extent of absorption or inhibit the metabolism of clarithromycin.  The mechanism of interaction is unknown but may be related to competition for intestinal CYP450 3A4 and/or absorptive sites.', 'grapefruit juice', 'Minor', 'Concomitant use of Clarithromycin with grapefruit or grapefruit juice should generally be avoided.', NULL, NULL, 'DDInter', 0);
