INSERT OR REPLACE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (4422, 2886, 'Colchicine', 'Coadministration with grapefruit juice may increase the serum concentrations of colchicine.  Clinical toxicity including myopathy, neuropathy, multiorgan failure, and pancytopenia may occur.  The proposed mechanism is inhibition of CYP450 3A4-mediated first-pass metabolism and P-glycoprotein efflux in the gut wall by certain compounds present in grapefruits.', 'grapefruit juice', 'Major', 'Patients treated with colchicine should be advised to avoid the consumption of grapefruit and grapefruit juice, and to contact their physician if they experience symptoms of colchicine toxicity such as abdominal pain, nausea, vomiting, diarrhea, fatigue, myalgia, asthenia, hyporeflexia, paresthesia, and numbness.', NULL, NULL, 'DDInter', 0);
