INSERT OR REPLACE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (4460, 20833, 'Aliskiren', 'Coadministration with orange, apple, or grapefruit juice may significantly decrease the oral bioavailability and renin-inhibiting effect of aliskiren.  The exact mechanism of interaction is unknown, but may include inhibition of OATP2B1-mediated influx of aliskiren in the small intestine, formation of insoluble complexes between fruit juice constituents and aliskiren, and/or increased ionization of aliskiren due to reduced intestinal pH.', 'grapefruit juice', 'Moderate', 'To ensure steady systemic drug levels and therapeutic effects, patients should establish a routine pattern for administration of aliskiren with regard to meals.  Coadministration with orange, apple, or grapefruit juice should be avoided, especially if these juices are to be consumed on a regular basis or shortly before or after aliskiren dosing.', NULL, NULL, 'DDInter', 0);
INSERT OR REPLACE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (4461, 20833, 'Aliskiren', 'Coadministration with orange, apple, or grapefruit juice may significantly decrease the oral bioavailability and renin-inhibiting effect of aliskiren.  The exact mechanism of interaction is unknown, but may include inhibition of OATP2B1-mediated influx of aliskiren in the small intestine, formation of insoluble complexes between fruit juice constituents and aliskiren, and/or increased ionization of aliskiren due to reduced intestinal pH.', 'orange juice', 'Moderate', 'To ensure steady systemic drug levels and therapeutic effects, patients should establish a routine pattern for administration of aliskiren with regard to meals.  Coadministration with orange, apple, or grapefruit juice should be avoided, especially if these juices are to be consumed on a regular basis or shortly before or after aliskiren dosing.', NULL, NULL, 'DDInter', 0);
INSERT OR REPLACE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (4462, 0, 'Dabrafenib', 'Food may reduce as well as delay the absorption of dabrafenib.  In study subjects, administration of dabrafenib with a high-fat meal decreased peak plasma concentration (Cmax) and systemic exposure (AUC) by 51% and 31%, respectively, and delayed median Tmax by approximately 3.6 hours compared to administration in the fasted state.', 'high-fat foods', 'Moderate', 'Dabrafenib should be taken at least 1 hour before or 2 hours after a meal.', NULL, NULL, 'DDInter', 0);
INSERT OR REPLACE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (4463, 963, 'Cyclosporine', 'Administration with grapefruit juice (compared to water or orange juice) has been shown to increase blood concentrations of cyclosporine with a relatively high degree of interpatient variability.  The mechanism is inhibition of CYP450 3A4-mediated first-pass metabolism in the gut wall by certain compounds present in grapefruits.', 'grapefruit juice', 'Moderate', 'Patients receiving cyclosporine therapy should be advised to either refrain from or avoid fluctuations in the consumption of grapefruits and grapefruit juice.  Until more data are available, the consumption of red wine or purple grape juice should preferably be avoided or limited.  All oral formulations of cyclosporine should be administered on a consistent schedule with regard to time of day and relation to meals so as to avoid large fluctuations in plasma drug levels.', NULL, NULL, 'DDInter', 0);
