INSERT OR REPLACE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (4795, 3681, 'Digoxin', 'Administration of digoxin with a high-fiber meal has been shown to decrease its bioavailability by almost 20%.  Fiber can sequester up to 45% of the drug when given orally.', 'high-fiber meal', 'Minor', 'Patients should be advised to maintain a regular diet without significant fluctuation in fiber intake while digoxin is being titrated.', NULL, NULL, 'DDInter', 0);
INSERT OR REPLACE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (4796, 0, 'Diethylpropion', 'Alcohol may potentiate the central nervous system and cardiovascular effects of centrally-acting appetite suppressants.', 'alcohol', 'Moderate', 'Concomitant use of centrally-acting appetite suppressants and alcohol should be avoided if possible, especially in patients with a history of cardiovascular disease.  Patients should be counselled to avoid hazardous activities requiring complete mental alertness and motor coordination until they know how these agents affect them, and to notify their physician if they experience excessive or prolonged CNS effects that interfere with their normal activities.', NULL, NULL, 'DDInter', 0);
INSERT OR REPLACE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (4797, 13982, 'Dicloxacillin', 'Certain penicillins may exhibit reduced gastrointestinal absorption in the presence of food.  The therapeutic effect of the antimicrobial may be reduced.', 'food', 'Moderate', 'The interacting penicillin should be administered one hour before or two hours after meals.  Penicillin V and amoxicillin are not affected by food and may be given without regard to meals.', NULL, NULL, 'DDInter', 0);
