INSERT OR REPLACE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (4799, 0, 'Didanosine', 'Didanosine bioavailability is decreased when administered with food.  Loss of efficacy may result.', 'food', 'Moderate', 'Didanosine should be administered in the fasting state, at least 30 minutes before or more than 2 hours after eating.', NULL, NULL, 'DDInter', 0);
INSERT OR REPLACE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (4800, 10774, 'Dicyclomine', 'Use of anticholinergic agents with alcohol may result in sufficient impairment of attention so as to render driving and operating machinery more hazardous.  In addition, the potential for abuse may be increased with the combination.  The mechanism of interaction is not established but may involve additive depressant effects on the central nervous system.', 'alcohol', 'Moderate', 'Alcohol should generally be avoided during therapy with anticholinergic agents.  Patients should be counseled to avoid activities requiring mental alertness until they know how these agents affect them.', NULL, NULL, 'DDInter', 0);
