INSERT OR REPLACE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (4852, 21657, 'Aluminum hydroxide', 'The concomitant administration of aluminum-containing products (e.g., antacids and phosphate binders) and citrates may significantly increase serum aluminum concentrations, resulting in toxicity.  Citrates or citric acid are contained in numerous soft drinks, citrus fruits, juices, and effervescent and dispersible drug formulations.  Citrates enhance the gastrointestinal absorption of aluminum by an unknown mechanism, which may involve the formation of a soluble aluminum-citrate complex.', 'citrus fruits', 'Major', 'Some experts recommend that antacids should not be mixed with or given after high protein formulations, that the antacid dose should be separated from the feeding by as much as possible, and that the tube should be thoroughly flushed before administration.', NULL, NULL, 'DDInter', 0);
INSERT OR REPLACE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (4853, 21657, 'Aluminum hydroxide', 'The concomitant administration of aluminum-containing products (e.g., antacids and phosphate binders) and citrates may significantly increase serum aluminum concentrations, resulting in toxicity.  Citrates or citric acid are contained in numerous soft drinks, citrus fruits, juices, and effervescent and dispersible drug formulations.  Citrates enhance the gastrointestinal absorption of aluminum by an unknown mechanism, which may involve the formation of a soluble aluminum-citrate complex.', 'soft drinks', 'Major', 'Some experts recommend that antacids should not be mixed with or given after high protein formulations, that the antacid dose should be separated from the feeding by as much as possible, and that the tube should be thoroughly flushed before administration.', NULL, NULL, 'DDInter', 0);
INSERT OR REPLACE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (4854, 0, 'Alvimopan', 'Coadministration with a high-fat meal may decrease the rate and extent of alvimopan absorption.', 'high-fat foods', 'Minor', 'Alvimopan should be administered on an empty stomach, at least 2 hours after or 1 hour before a meal.  Patients should avoid consumption of high-fat foods during treatment with Alvimopan.', NULL, NULL, 'DDInter', 0);
INSERT OR REPLACE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (4855, 28230, 'Dolutegravir', 'Food increases the extent of absorption and slows the rate of absorption of dolutegravir.  When administered with a low-, moderate- or high-fat meal, dolutegravir peak plasma concentration (Cmax) increased by 46%, 52% and 67%, systemic exposure (AUC) increased by 33%, 41% and 66%, and time to reach Cmax (Tmax) increased from 2 hours to 3, 4 and 5 hours, respectively, compared to administration under fasted conditions.', 'food', 'Minor', 'Dolutegravir may be taken with or without food.', NULL, NULL, 'DDInter', 0);
INSERT OR REPLACE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (4856, 0, 'Dofetilide', 'In vitro data suggest that grapefruit juice may inhibit the CYP450 3A4 first-pass metabolism of dofetilide.', 'grapefruit juice', 'Minor', 'Concomitant use of Dofetilide with grapefruit or grapefruit juice should generally be avoided.', NULL, NULL, 'DDInter', 0);
INSERT OR REPLACE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (4857, 0, 'Doxepin', 'Concomitant use of ethanol and a tricyclic antidepressant (TCA) may result altered TCA plasma levels and efficacy, and additive impairment of motor skills, especially driving skills.  Acute ethanol ingestion may inhibit TCA metabolism, while chronic ingestion of large amounts of ethanol may induce hepatic TCA metabolism.', 'alcohol', 'Moderate', 'Patients should be advised to avoid alcohol during TCA therapy.  Alcoholics who have undergone detoxification should be monitored for decreased TCA efficacy.  Dosage adjustments may be required.', NULL, NULL, 'DDInter', 0);
