INSERT OR REPLACE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (4929, 25974, 'Eltrombopag', 'Food may reduce the oral bioavailability of eltrombopag. The calcium content of this meal may have also contributed to this decrease in exposure.  In another study, adult subjects administered a single 25 mg dose of eltrombopag for oral suspension with a high-calcium, moderate-fat, moderate-calorie meal exhibited a 79% decrease in Cmax and 75% decrease in AUC of eltrombopag.  Administration of eltrombopag 2 hours after the high-calcium meal decreased eltrombopag Cmax by 48% and AUC by 47%, while administration 2 hours before the high-calcium meal decreased eltrombopag Cmax by 14% and AUC by 20%.', 'dairy products', 'Moderate', 'Eltrombopag should be taken on an empty stomach one hour before or two hours after a meal.  Additionally, eltrombopag should be taken at least 2 hours before or 4 hours after any products that contain polyvalent cations such as antacids, mineral supplements, dairy products, and fortified juices.', NULL, NULL, 'DDInter', 0);
