INSERT OR REPLACE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (5238, 29189, 'Abemaciclib', 'Grapefruit juice may increase the plasma concentrations of abemaciclib.  The proposed mechanism is inhibition of CYP450 3A4-mediated first-pass metabolism in the gut wall by certain compounds present in grapefruit.  Inhibition of hepatic CYP450 3A4 may also contribute.  The interaction has not been studied with grapefruit juice, but has been reported for other CYP450 3A4 inhibitors.', 'grapefruit juice', 'Moderate', 'Abemaciclib may be administered with or without food.  Patients should avoid consumption of grapefruit and grapefruit juice during treatment with abemaciclib.', NULL, NULL, 'DDInter', 0);
