INSERT OR REPLACE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (5445, 19247, 'Fluvastatin', 'Concomitant use of statin medication with substantial quantities of alcohol may increase the risk of hepatic injury.  Transient increases in serum transaminases have been reported with statin use and while these increases generally resolve or improve with continued therapy or a brief interruption in therapy, there have been rare postmarketing reports of fatal and non-fatal hepatic failure in patients taking statins.  Patients who consume substantial quantities of alcohol and/or have a history of liver disease may be at increased risk for hepatic injury.', 'alcohol', 'Moderate', 'Patients should be counseled to avoid substantial quantities of alcohol in combination with statin medications and clinicians should be aware of the increased risk for hepatotoxicity in these patients.', NULL, NULL, 'DDInter', 0);
