INSERT OR REPLACE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (5683, 0, 'Amobarbital', 'Concurrent acute use of barbiturates and ethanol may result in additive CNS effects,  including impaired coordination, sedation, and death.  Tolerance of these agents may occur with chronic use.  The mechanism is related to inhibition of microsomal enzymes acutely and induction of hepatic microsomal enzymes chronically.', 'alcohol', 'Major', 'The combination of ethanol and barbiturates should be avoided.', NULL, NULL, 'DDInter', 0);
INSERT OR REPLACE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (5684, 3236, 'Ganciclovir', 'Food delays but enhances the oral absorption and bioavailability of ganciclovir capsules, possibly due to prolongation of gastrointestinal transit time.', 'high-fat foods', 'Moderate', 'To ensure maximal oral absorption, oral ganciclovir should be administered with or immediately after a meal.', NULL, NULL, 'DDInter', 0);
