INSERT OR REPLACE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (5743, 0, 'Glyburide', 'Alcohol may cause hypoglycemia or hyperglycemia in patients with diabetes.  Hypoglycemia most frequently occurs during acute consumption of alcohol.  Even modest amounts can lower blood sugar significantly, especially when the alcohol is ingested on an empty stomach or following exercise.  The mechanism involves inhibition of both gluconeogenesis as well as the counter-regulatory response to hypoglycemia.  Episodes of hypoglycemia may last for 8 to 12 hours after ethanol ingestion.  By contrast, chronic alcohol abuse can cause impaired glucose tolerance and hyperglycemia.', 'alcohol', 'Moderate', 'Patients with diabetes should avoid consuming alcohol if their blood glucose is not well controlled, or if they have hypertriglyceridemia, neuropathy, or pancreatitis.  Patients with well controlled diabetes should limit their alcohol intake to one drink daily for women and two drinks daily for men (1 drink = 5 oz wine, 12 oz beer, or 1.5 oz distilled spirits) in conjunction with their normal meal plan.  Alcohol should not be consumed on an empty stomach or following exercise.', NULL, NULL, 'DDInter', 0);
INSERT OR REPLACE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (5744, 0, 'Amphetamine', 'Alcohol may potentiate the cardiovascular effects of amphetamines.', 'alcohol', 'Moderate', 'Concomitant use of amphetamines and alcohol should be avoided if possible, especially in patients with a history of heart disease.', NULL, NULL, 'DDInter', 0);
INSERT OR REPLACE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (5745, 5744, 'Griseofulvin', 'Isolated case reports have suggested that the ingestion of alcohol during griseofulvin therapy may rarely cause disulfiram-like reactions, flushing, tachycardia, or increased effects of alcohol.', 'alcohol', 'Moderate', 'Patients should be advised of the possibility of increased adverse effects or a disulfiram-like reaction.', NULL, NULL, 'DDInter', 0);
