INSERT OR REPLACE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (5765, 0, 'Hydrocodone', 'Alcohol may potentiate the central nervous system (CNS) depressant effects of opioid analgesics including hydrocodone.  Concomitant use may result in additive CNS depression and impairment of judgment, thinking, and psychomotor skills.  In more severe cases, hypotension, respiratory depression, profound sedation, coma, or even death may occur.', 'alcohol', 'Major', 'Patients taking sustained-release formulations of hydrocodone should not consume alcohol or use medications that contain alcohol.  In general, potent narcotics such as hydrocodone should not be combined with alcohol.  Patients should also avoid consumption of grapefruit or grapefruit juice during treatment with hydrocodone.', NULL, NULL, 'DDInter', 0);
