INSERT OR REPLACE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (5816, 0, 'Ibandronate', 'Food significantly decreases the bioavailability of oral ibandronate.', 'food', 'Moderate', 'Oral ibandronate should be administered on an empty stomach (after an overnight fast of at least 6 hours) with a 180 to 240 mL glass of plain water and no other food or drink should be taken for at least 30 minutes after administration.', NULL, NULL, 'DDInter', 0);
INSERT OR REPLACE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (5817, 30261, 'Acalabrutinib', 'Grapefruit juice may increase the plasma concentrations of acalabrutinib.  The proposed mechanism is inhibition of CYP450 3A4-mediated first-pass metabolism in the gut wall by certain compounds present in grapefruit.  Inhibition of hepatic CYP450 3A4 may also contribute.  The interaction has not been studied with grapefruit juice, but has been reported for other CYP450 3A4 inhibitors.', 'grapefruit juice', 'Major', 'Acalabrutinib may be administered with or without food.  Patients should avoid consumption of grapefruit and grapefruit juice during treatment with acalabrutinib.', NULL, NULL, 'DDInter', 0);
INSERT OR REPLACE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (5818, 20724, 'Ibrutinib', 'Coadministration with grapefruit, grapefruit juice, or Seville oranges may significantly increase the plasma concentrations of ibrutinib.  The proposed mechanism is inhibition of CYP450 3A4-mediated first-pass metabolism in the gut wall by certain compounds present in grapefruit.', 'grapefruit juice', 'Major', 'Patients treated with ibrutinib should avoid consumption of Seville oranges, grapefruit, grapefruit juice, and any supplement containing grapefruit extract.  Ibrutinib should be taken once daily at approximately the same time each day.', NULL, NULL, 'DDInter', 0);
