INSERT OR REPLACE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (5820, 10785, 'Hyoscyamine', 'Use of anticholinergic agents with alcohol may result in sufficient impairment of attention so as to render driving and operating machinery more hazardous.  In addition, the potential for abuse may be increased with the combination.  The mechanism of interaction is not established but may involve additive depressant effects on the central nervous system.', 'alcohol', 'Moderate', 'Alcohol should generally be avoided during therapy with anticholinergic agents.  Patients should be counseled to avoid activities requiring mental alertness until they know how these agents affect them.', NULL, NULL, 'DDInter', 0);
INSERT OR REPLACE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (5821, 429, 'Ibuprofen', 'The concurrent use of aspirin or nonsteroidal anti-inflammatory drugs (NSAIDs) and ethanol may lead to gastrointestinal (GI) blood loss.  The mechanism may be due to a combined local effect as well as inhibition of prostaglandins leading to decreased integrity of the GI lining.', 'alcohol', 'Moderate', 'Patients should be counseled on this potential interaction and advised to refrain from alcohol consumption while taking aspirin or NSAIDs.', NULL, NULL, 'DDInter', 0);
