INSERT OR REPLACE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (5997, 1408, 'Iron', 'Concomitant use of some food may reduce the bioavailability of orally administered iron, and vice versa.', 'food', 'Moderate', 'Ideally, iron products should be taken on an empty stomach (i.e., at least 1 hour before or 2 hours after meals), but if this is not possible, administer with meals and monitor the patient more closely for a subtherapeutic effect.  Some studies suggest administration of iron with ascorbic acid may enhance bioavailability.  In addition, administration of oral iron products and some oral medications should be separated whenever the bioavailability of either agent may be decreased.  Consult the product labeling for specific separation times and monitor clinical responses as appropriate.', NULL, NULL, 'DDInter', 0);
