INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (199, 'Zalcitabine', 'Lamivudine', 'Major', 'Concomitant use of the cytidine analog nucleoside reverse transcriptase inhibitors (NRTI) lamivudine and emtricitabine may inhibit the intracellular phosphorylation of one another to their respective active derivative in vivo. This could result in diminished antiretroviral effects of these drugs. However, clinical experience on the coadministration of cytidine analogs is lacking. This interaction may also occur with zalcitabine. In addition, the therapeutic efficacy of these drugs in combination appears limited, since lamivudine and emtricitabine have similar resistance profiles via mutation of the same viral reverse transcriptase gene (M184V).', 'DDInter', 'The use of the cytidine analog NRTIs lamivudine, emtricitabine, or zalcitabine in any combination in an antiretroviral treatment regimen that consists of two NRTIs is not recommended. Local antiretroviral treatment experts should be consulted for current practice.', 'Antagonism', 'The use of the cytidine analog NRTIs lamivudine, emtricitabine, or zalcitabine in any combination in an antiretroviral treatment regimen that consists of two NRTIs is not recommended.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/199/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] Veal GJ, Hoggard PG, Barry MG, Khoo S, Back DJ "Interaction between lamivudine (3TC) and other nucleoside analogues for intracellular phosphorylation." AIDS 10 (1996): 546-8[4] "Product Information. Epivir (lamivudine)." Glaxo Wellcome, Research Triangle Park, NC.[5] Department of Health andHuman Services "Guidelines for the Use of Antire