INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (941, 'Abatacept', 'Tofacitinib', 'Major', 'Coadministration of tofacitinib with other immunosuppressive agents may potentiate the risk of infections as well as lymphoma and other malignancies. Serious and sometimes fatal infections due to bacterial, mycobacterial, invasive fungal, viral, or other opportunistic pathogens have been reported in patients receiving tofacitinib, most of whom were taking concomitant immunosuppressants such as methotrexate or corticosteroids.', 'DDInter', 'Tofacitinib should not be used in combination with other Janus kinase inhibitors, biologic disease-modifying antirheumatic drugs (DMARDs), or potent immunosuppressants (e.g., azathioprine, cyclosporine). Patients receiving tofacitinib should be closely monitored for the development of signs and symptoms of infection during and after treatment, including the possible development of tuberculosis in patients who tested negative for latent tuberculosis infection prior to initiating therapy. If a serious infection, an opportunistic infection, or sepsis develops, tofacitinib should be interrupted until the infection is controlled.', 'Others', 'Tofacitinib should not be used in combination with other Janus kinase inhibitors, biologic disease-modifying antirheumatic drugs (DMARDs), or potent immunosuppressants (e.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/941/', '[1] "Product Information. Xeljanz (tofacitinib)." Pfizer U.S. Pharmaceuticals Group, New York, NY.', 'Apremilast, Pirfenidone, Belumosudil, Teprotumumab, Emapalumab, Baricitinib, Tofacitinib, Ravulizumab, Pegcetacoplan, Eculizumab, Sutimlimab', 'Cyclosporine, Antithymocyte immunoglobulin (rabbit), Secukinumab, Ravulizumab, Mycophenolic acid, Methotrexate, Tacrolimus, Abatacept, Apremilast, Inebilizumab, Belumosudil, More', 1767369485);
