INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (968, 'Abciximab', 'Bivalirudin', 'Moderate', 'The safety and efficacy of bivalirudin have not been established when used in conjunction with platelet inhibitors other than aspirin. Theoretically, the concomitant administration of bivalirudin (plus aspirin) and platelet inhibitors may increase the risk of bleeding complications due to additive or synergistic effects on the clotting cascade. However, limited data from drug interaction studies do not suggest pharmacodynamic interactions with the platelet inhibitors, ticlopidine and abciximab.', 'DDInter', 'Until further data are available, the coadministration of bivalirudin and antiplatelet agents, and possibly other agents with antiplatelet activity such as nonsteroidal anti-inflammatory drugs (NSAIDs), should probably be undertaken with caution. Close clinical and laboratory observation for bleeding complications is recommended.', 'Synergism', 'Until further data are available, the coadministration of bivalirudin and antiplatelet agents, and possibly other agents with antiplatelet activity such as nonsteroidal anti-inflammatory drugs (NSAIDs), should probably be undertaken with caution.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/968/', '[1] "Product Information. Angiomax (bivalirudin)" The Medicines Compny, Cambridge, MA.', 'Abciximab, Antithrombin III human, Protein C, Acenocoumarol, Selexipag', 'Antithrombin III human, Bivalirudin, Acenocoumarol, Selexipag, Acetylsalicylic acid, Cangrelor', 1767369485);
