INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (4189, 'Isoprenaline', 'Acebutolol', 'Moderate', 'Some beta-blockers may antagonize the bronchodilatory, hypotensive, and tachycardic effects of isoproterenol. The mechanism is blockade of beta-adrenergic receptors, which leads to bronchoconstriction, vasodilation, and increased heart rate. Beta-blockers have been used successfully to treat catecholamine or isoproterenol-induced tachyarrhythmias.', 'DDInter', 'This combination should generally be avoided. Patients who are receiving isoproterenol for cardiac conditions should be closely monitored for adequate therapeutic effect if a beta blocker is added. If no alternative exists, small doses of a B-1 selective beta-blocker (e.g., acebutolol, atenolol, betaxolol, bisoprolol, or metoprolol) may be preferable; however, respiratory status should be closely monitored in patients with obstructive pulmonary disease.', 'Antagonism', 'This combination should generally be avoided.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/4193/', '[1] Ziegler MG, Chernow B, Woodson LC, Coyle J, Cruess D, Lake CR "The effect of propranolol on catecholamine clearance." Clin Pharmacol Ther 40 (1986): 116-9[2] Pickles H, Perucca E, Fish A, Richens A "Propranolol and sotalol as antagonists of isoproterenol-enhanced physiologic tremor." Clin Pharmacol Ther 30 (1981): 303-10[3] Johnsson G, Svedmyr N, Thiringer G "Effects of intravenous propranolol and metoprolol and their interaction with isoprenaline on pulmonary function, heart rate and blood pressure in asthmatics." Eur J Clin Pharmacol 8 (1975): 175-80[4] Thiringer G, Svedmyr N "Interaction of orally administered metoprolol, practolol and propranolol with isoprenaline in asthmatics." Eur J Clin Phar