INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (4317, 'Quinidine', 'Acebutolol', 'Moderate', 'Quinidine may increase serum levels of some oral beta-blockers. The proposed mechanism is inhibition of CYP450 2D6 first-pass metabolism and decreased hepatic clearance of hepatically metabolized beta-blockers. Quinidine and beta-blockers may also have additive negative inotropic effects, which can be beneficial when used to treat atrial fibrillation. However, adverse effects have been reported with quinidine and propranolol.', 'DDInter', 'Patients should be monitored for orthostatic hypotension, cardiac arrhythmias, bradycardia, and heart failure, and beta-blocker dosage should be decreased if necessary.', 'Metabolism', 'Patients should be monitored for orthostatic hypotension, cardiac arrhythmias, bradycardia, and heart failure, and beta-blocker dosage should be decreased if necessary.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/4321/', '[1] Edeki TI, He H, Wood AJ "Pharmacogenetic explanation for excessive B-blockade following timolol eye drops." JAMA 274 (1995): 1611-3[2] Leemann T, Dayer P, Meyer UA "Single-dose quinidine treatment inhibits metoprolol oxidation in extensive metabolizers." Eur J Clin Pharmacol 29 (1986): 739-41[3] Loon NR, Wilcox CS, Folger W "Orthostatic hypotension due to quinidine and propranolol." Am J Med 81 (1986): 1101-4[4] Manolis AS, Estes NA "Orthostatic hypotension due to quinidine and atenolol." Am J Med 82 (1987): 1083-4[5] Kessler KM, Humphries WC, Black M, Spann JF "Quinidine pharmacokinetics in patients with cirrhosis or receiving propranolol." Am Heart J 96 (1978): 627-35[6] Ochs HR, Greenblatt DJ, Woo E, et al "Effect of propranolol on pharmacokinetics and acute electrocardiographic chan