INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (4734, 'Grepafloxacin', 'Caffeine', 'Moderate', 'Coadministration with certain quinolones may increase the plasma concentrations and pharmacologic effects of caffeine due to inhibition of the CYP450 1A2 metabolism of caffeine. Quinolones that may inhibit CYP450 1A2 include ciprofloxacin, enoxacin, grepafloxacin, nalidixic acid, norfloxacin, pipemidic acid, and pefloxacin (not all commercially available).', 'DDInter', 'Patients using caffeine-containing products should be advised that increased adverse effects such as headache, tremor, restlessness, nervousness, insomnia, tachycardia, and blood pressure increases may occur during coadministration with quinolones that inhibit CYP450 1A2. Caffeine intake should be limited when taking high dosages of these quinolones. If an interaction is suspected, other quinolones such as gatifloxacin, gemifloxacin, levofloxacin, lomefloxacin, moxifloxacin, and ofloxacin may be considered, since they are generally believed to have little or no effect on CYP450 1A2 or have been shown not to interact with caffeine.', 'Metabolism', 'Patients using caffeine-containing products should be advised that increased adverse effects such as headache, tremor, restlessness, nervousness, insomnia, tachycardia, and blood pressure increases may occur during coadministration with quinolones that inhibit CYP450 1A2.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/4738/', '[1] Carbo ML, Segura J, De la Torre R, et al "Effect of quinolones on caffeine disposition." Clin Pharmacol Ther 45 (1989): 234-40[2] Harder S, Fuhr U, Staib AH, Wolf T "Ciprofloxacin-caffeine: a drug interaction established using in vivo and in vitro investigations." Am J Med 87 (1989): 89-91[3