INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (12807, 'Ethanol', 'Temazepam', 'Moderate', 'Acute ethanol ingestion may potentiate the CNS effects of many benzodiazepines. Tolerance may develop with chronic ethanol use. The mechanism may be decreased clearance of the benzodiazepines because of CYP450 hepatic enzyme inhibition. Also, it has been suggested that the cognitive deficits induced by benzodiazepines may be increased in patients who chronically consume large amounts of alcohol.', 'DDInter', 'Patients should be advised to avoid alcohol during benzodiazepine therapy.', 'Metabolism', 'Patients should be advised to avoid alcohol during benzodiazepine therapy.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/12811/', '[1] Divoll M, Greenblatt DJ, Lacasse Y, Shader RI "Benzodiazepine overdosage: plasma concentrations and clinical outcome." Psychopharmacology (Berl) 73 (1981): 381-3[2] Ochs HR, Greenblatt DJ, Arendt RM, Hubbel W, Shader RI "Pharmacokinetic noninteraction of triazolam and ethanol." J Clin Psychopharmacol 4 (1984): 106-7[3] Staak M, Raff G, Nusser W "Pharmacopsychological investigations concerning the combined effects of dipotassium clorazepate and ethanol." Int J Clin Pharmacol Biopharm 17 (1979): 205-12[4] MacLeod SM, Giles HG, Patzalek G, Thiessen JJ, Sellers EM "Diazepam actions and plasma concentrations following ethanol ingestion." Eur J Clin Pharmacol 11 (1977): 345-9[5] Juhl RP, Van Thiel DH, Dittert LW, Smith RB "Alprazolam pharmacokinetics in alcoholic liver disease." J Clin Pharmacol 24 (1984): 113-9[6] Nichols JM, Martin F, Kirkby KC "A comparison of the effect of lorazepam on memory in heavy and low social drinkers." Psychopharmacology (Berl) 112 (1993): 475-82[7] Whiting B, Lawrence JR, Skelle