INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (16374, 'Alfuzosin', 'Riociguat', 'Moderate', 'Riociguat may potentiate the action of antihypertensive agents and other vasodilators. Symptomatic hypotension may occur. Riociguat stimulates soluble guanylate cyclase (sGC), an enzyme in the cardiopulmonary system that binds with nitric oxide (NO) to catalyze the synthesis of cyclic guanosine monophosphate (cGMP). The resultant accumulation of cGMP in vascular smooth muscle cells leads to peripheral vasodilation and blood pressure decreases.', 'DDInter', 'Caution is advised if riociguat is prescribed in combination with antihypertensive agents or other vasodilators.', 'Synergism', 'Caution is advised if riociguat is prescribed in combination with antihypertensive agents or other vasodilators.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/16389/', '[1] "Product Information. Adempas (riociguat)." Bayer Pharmaceutical Inc, West Haven, CT.[2] "Product Information. Adempas (riociguat)." Bayer Pharmaceutical Inc, West Haven, CT.', 'Finasteride, Dutasteride, Solifenacin', 'Bosentan, Ambrisentan, Macitentan, Metyrosine', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (16375, 'Alfuzosin', 'Risperidone', 'Moderate', 'Neuroleptic agents may potentiate the hypotensive effect of alfuzosin secondary to their peripheral alpha1-adrenergic blocking activity. Orthostatic hypotension and syncope associated with vasodilation may occur, particularly during the initial dose-titration period of neuroleptic therapy. In addition, alfuzosin may cause modest prolongation of the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval including many neuroleptics may result in elevated risk of ventricular arrhythmias, including ventricular tachycardia and torsade de pointes, because of additive arrhythmogenic potential related to their effects on the QT interval.', 'DDInter', 'Close clinical monitoring for adverse effects is recommended during concurrent therapy. A lower starting dosage and slower titration of the neuroleptic agent may be appropriate in patients receiving alfuzosin, especially if they are elderly. Patients should be advised to notify their physician if they experience orthostatic symptoms (dizziness, lightheadedness, syncope), and to seek medical help if they experience palpitations and fast/ or irregular heartbeats.', 'Synergism', 'Close clinical monitoring for adverse effects is recommended during concurrent therapy.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/16390/', '[1] "Product Information. Geodon (ziprasidone)." Pfizer US Pharmaceuticals, New York, NY.[2] Markowitz JS, Wells BG, Carson WH "Interactions between antipsychotic and antihypertensive drugs." Ann Pharmacother 29 (1995): 603-9[3] "Product Information. Risperdal (risperidone)." Janssen Pharmaceutica, Titusville, NJ.[4] "Product Information. Risperdal (risperidone)." Janssen Pharmaceutica, Titusville, NJ.[5] "Product Information. Uroxatral (alfuzosin)." sanofi-aventis , Bridgewater, NJ.', 'Silodosin, Tadalafil', NULL, 1767369485);
