INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (16743, 'Aliskiren', 'Atorvastatin', 'Moderate', 'Coadministration with atorvastatin may increase the plasma concentrations of aliskiren. The exact mechanism of interaction is unknown but may involve competitive inhibition of the CYP450 3A4 metabolism of aliskiren.', 'DDInter', 'Caution is advised during coadministration of aliskiren with atorvastatin. Antihypertensive response should be monitored more closely following initiation, discontinuation, or change of dosage of atorvastatin in patients who are stabilized on their aliskiren regimen. Patients should be advised to notify their physician if they experience excessive adverse effects of aliskiren such as dizziness, lightheadedness, diarrhea, abdominal pain, and gastroesophageal reflux.', 'Metabolism', 'Caution is advised during coadministration of aliskiren with atorvastatin.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/16758/', '[1] "Product Information. Tekturna (aliskiren)." Novartis Pharmaceuticals, East Hanover, NJ.[2] Richter WO, Jacob BG, Schwandt P "Interaction between fibre and lovastatin." Lancet 338 (1991): 706[3] Lilja JJ, Kivisto KT, Neuvonen PJ "Grapefruit juice increases serum concentrations of atorvastatin and has no effect on pravastatin." Clin Pharmacol Ther 66 (1999): 118-27[4] Boberg M, Angerbauer R, Fey P, Kanhai WK, Karl W, Kern A, Ploschke J, Radtke M "Metabolism of cerivastatin by human liver microsomes in vitro. Characterization of primary metabolic pathways and of cytochrome P45 isozymes involved." Drug Metab Dispos 25 (1997): 321-31[5] Bailey DG, Malcolm J, Arnold O, Spence JD "Grapefruit juice-drug interactions." Br J Clin Pharmacol 46 (1998): 101-10[6] Neuvonen PJ, Backman JT, Niemi M "Phar