INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (20438, 'Ambrisentan', 'Neratinib', 'Moderate', 'Coadministration with neratinib may increase the plasma concentrations of drugs that are substrates of the P-glycoprotein (P-gp) transporter. The proposed mechanism is decreased clearance in the intestine, liver, and/or kidney due to inhibition of P-glycoprotein-mediated drug efflux by neratinib.', 'DDInter', 'Caution is advised when neratinib is used concurrently with drugs that are known P-gp substrates, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever neratinib is added to or withdrawn from therapy.', 'Others', 'Caution is advised when neratinib is used concurrently with drugs that are known P-gp substrates, particularly those with a narrow therapeutic range.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/20453/', '[1] "Product Information. Nerlynx (neratinib)." Puma Biotechnology, Inc., Los Angeles, CA.[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] Abbas R, Hug BA, Leister C, Burns J, Sonnichsen D "Pharmacokinetics of oral neratinib during co-administration of ketoconazole in healthy subjects." Br J Clin Pharmacol 71 (2011): 522-7[4] "Product Information. Nerlynx (neratinib)." Puma Biotechnology, Inc., Los Angeles, CA.', 'Riociguat, Macitentan, Metyrosine, Tadalafil', 'Imatinib, Acalabrutinib, Dacomitinib, Pacritinib, Fedratinib, Avapritinib, Larotrectinib, Bosutinib, Selumetinib, Ribociclib, Vandetanib, More', 1767369485);
