INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (20448, 'Ambrisentan', 'Remdesivir', 'Moderate', 'Concomitant use of remdesivir with other agents that are known to induce hepatotoxicity may theoretically increase the risk of liver injury. Data from investigational use and clinical studies suggest that remdesivir may be associated with hepatotoxic effects.', 'DDInter', 'Until more information is available, concomitant use of remdesivir with known hepatotoxic drugs should be avoided when possible. Liver function should be evaluated prior to starting remdesivir and monitored during treatment as clinically appropriate. Remdesivir should not be initiated in patients with ALT greater than or equal to 5 times the upper limit of normal (ULN) at baseline. Likewise, remdesivir should be discontinued immediately if ALT increases to 5 times ULN or greater during treatment (may be restarted when ALT falls below 5 times ULN), or if ALT elevation is accompanied by signs or symptoms of liver inflammation or increasing conjugated bilirubin, alkaline phosphatase, or INR.', 'Synergism', 'Until more information is available, concomitant use of remdesivir with known hepatotoxic drugs should be avoided when possible.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/20463/', '[1] Gilead Sciences, Inc "About Remdesivir. Available from: URL: https://www.gilead.com/purpose/advancing-global-health/covid-19/about-remdesivir." ([2020]):[2] US Food and Drug Administration "Fact sheet for health care providers emergency use authorization (EUA) of remdesivir (GS-5734TM) Available from: URL: https://www.fda.gov/media/137566/download." ([2020, May 1]]):[3] European Medicines Agency "Summary on compassionate use. Remdesivir Gilead. Available from: URL: https://www.ema.europa.eu/en/documents/other/summary-compassionate-use-remdesivir-gilead_en.pdf." ([2020 Apr 03]):[4] Cerner Multum, Inc. "Australian Product Information." O 0[5] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0', 'Riociguat, Metyrosine, Tadalafil', 'Lamivudine, Paritaprevir, Glecaprevir, Tenofovir alafenamide, Maraviroc, Simeprevir, Cabotegravir, Valaciclovir, Zanamivir, Lenacapavir, Velpatasvir, More', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (20449, 'Ambrisentan', 'Rifampicin', 'Moderate', 'Acute, but not long-term, coadministration of rifampin increases the plasma concentrations of ambrisentan. Rifampin is an inhibitor of organic anion transporting polypeptide (OATP), a strong inducer of CYP450 3A4 and 2C19, and an inducer of P-glycoprotein and uridine diphosphate glucuronosyltransferases (UGTs), all of which have been shown in vitro to be involved in the metabolic clearance of ambrisentan.', 'DDInter', 'Patients receiving ambrisentan therapy should be closely monitored when starting treatment with rifampin. Because ambrisentan is associated with dose-related hepatotoxicity, patients should be advised to notify their physician if they experience signs and symptoms of liver injury such as fever, rash, anorexia, nausea, vomiting, fatigue, right upper quadrant pain, dark urine, and jaundice. Patients should also be monitored for anemia, fluid retention, and pulmonary edema.', 'Metabolism', 'Patients receiving ambrisentan therapy should be closely monitored when starting treatment with rifampin.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/20464/', '[1] "Product Information. Letairis (ambrisentan)." Gilead Sciences, Foster City, CA.[2] Harrison B, Magee MH, Mandagere A, et al. "Effects of rifampicin (rifampin) on the pharmacokinetics and safety of ambrisentan in healthy subjects: a single-sequence, open-label study." Clin Drug Investig 30 (2010): 875-85', 'Capreomycin, Pyrazinamide, Rifamycin, Sulfamethoxazole, Rifapentine, Streptomycin, Trimethoprim, Cycloserine, Rifabutin, Isoniazid, Ethambutol, More', 'Metyrosine', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (20450, 'Ambrisentan', 'Riociguat', 'Moderate', 'Riociguat may potentiate the action of antihypertensive agents and other vasodilators. Symptomatic hypotension may occur. Riociguat stimulates soluble guanylate cyclase (sGC), an enzyme in the cardiopulmonary system that binds with nitric oxide (NO) to catalyze the synthesis of cyclic guanosine monophosphate (cGMP). The resultant accumulation of cGMP in vascular smooth muscle cells leads to peripheral vasodilation and blood pressure decreases.', 'DDInter', 'Caution is advised if riociguat is prescribed in combination with antihypertensive agents or other vasodilators.', 'Synergism', 'Caution is advised if riociguat is prescribed in combination with antihypertensive agents or other vasodilators.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/20465/', '[1] "Product Information. Adempas (riociguat)." Bayer Pharmaceutical Inc, West Haven, CT.[2] "Product Information. Adempas (riociguat)." Bayer Pharmaceutical Inc, West Haven, CT.', 'Riociguat, Macitentan', 'Ambrisentan, Macitentan, Metyrosine', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (20451, 'Ambrisentan', 'Ritonavir', 'Moderate', 'In vitro data suggest that coadministration with potent inhibitors of P-glycoprotein (e.g., cyclosporine) and/or organic anion transporting polypeptide (e.g., cyclosporine, ritonavir) may increase the plasma concentrations of ambrisentan, which is a substrate of these transporters. The interaction has not been studied with other P-gp or OATP inhibitors.', 'DDInter', 'Because ambrisentan is associated with dose-related hepatotoxicity, concomitant use with potent inhibitors of P-gp and/or OATP should be approached with caution. Patients should be advised to notify their physician if they experience signs and symptoms of liver injury such as fever, rash, anorexia, nausea, vomiting, fatigue, right upper quadrant pain, dark urine, and jaundice.', 'Absorption', 'Because ambrisentan is associated with dose-related hepatotoxicity, concomitant use with potent inhibitors of P-gp and/or OATP should be approached with caution.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/20466/', '[1] "Product Information. Letairis (ambrisentan)." Gilead Sciences, Foster City, CA.[2] "Product Information. Norvir (ritonavir)." Abbott Pharmaceutical, Abbott Park, IL.', 'Lamivudine, Paritaprevir, Glecaprevir, Tenofovir alafenamide, Maraviroc, Simeprevir, Cabotegravir, Valaciclovir, Zanamivir, Lenacapavir, Velpatasvir, More', 'Metyrosine', 1767369485);
