INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (20810, 'Exenatide', 'Amikacin', 'Moderate', 'Drugs that affect renal function and/or hydration status may potentiate the adverse renal effects of exenatide. The mechanism of interaction is unknown. Exenatide has not been found to be directly nephrotoxic in preclinical or clinical studies.', 'DDInter', 'Clinicians should be aware of the potential for increased adverse renal effects of exenatide during coadministration with drugs that are known to affect renal function or hydration status.', 'Others', 'Clinicians should be aware of the potential for increased adverse renal effects of exenatide during coadministration with drugs that are known to affect renal function or hydration status.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/20825/', '[1] "Product Information. Byetta (exenatide)." Amylin Pharmaceuticals Inc, San Diego, CA.[2] "Product Information. Byetta (exenatide)." Amylin Pharmaceuticals Inc, San Diego, CA.', 'Tetracycline, Ganciclovir, Acyclovir, Rifamycin, Oxytetracycline, Famciclovir, Sulfamethizole, Cefuroxime, Trifluridine, Benzylpenicillin, Ampicillin, More', 'Glipizide, Acetohexamide, Guar gum, Pramlintide, Alogliptin, Rosuvastatin, Tolazamide, Empagliflozin, Dulaglutide, Tolbutamide, Miglitol, More', 1767369485);
