INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (22408, 'Amitriptyline', 'Flumazenil', 'Major', 'Severe adverse effects, including fatalities, have been reported following the administration of flumazenil to multiple-drug-overdose patients who have taken large quantities of tricyclic antidepressants. The effects have included seizures and arrhythmias (including ventricular tachycardia). The mechanism of this interaction may be related to a decrease in the protective antiseizure effects of benzodiazepines that patients may have taken concomitantly with overdoses of tricyclic antidepressants.', 'DDInter', 'Administration of flumazenil should be avoided in patients who are known or suspected to have received overdoses of tricyclic antidepressants.', 'Synergism', 'Administration of flumazenil should be avoided in patients who are known or suspected to have received overdoses of tricyclic antidepressants.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/22423/', '[1] Marchant B, Wray R, Leach A, Nama M "Flumazenil causing convulsions and ventricular tachycardia." BMJ 299 (1989): 860[2] Bodenham AR "Death after flumazenil." BMJ 299 (1989): 457[3] Mordel A, Winkler E, Almog S, Tirosh M, Ezra D "Seizures after flumazenil administration in a case of combined benzodiazepine and tricyclic antidepressant overdose." Crit Care Med 20 (1992): 1733-4[4] "Flumazenil." Med Lett Drugs Ther 34 (1992): 66-8[5] Lheureux P, Vrankx M, Askenasi R "Administration of flumazenil." Ann Emerg Med 20 (1991): 592-3[6] Geller E, Crome P, Schaller MD, Marchant B, Ectors M, Scollo-Lavizzari G "Risks and benefits of therapy with flumazenil (Anexate) in mixed drug intoxications." Eur Neurol 31 (1991): 241-50', 'Citalopram, Sertraline, Fluoxetine, St. John''s Wort