INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (22769, 'Amitriptyline', 'Xylometazoline (nasal)', 'Moderate', 'Coadministration with tri- or tetra-cyclic antidepressants may theoretically potentiate the sympathomimetic effects (e.g., hypertension, tachycardia) of systemically absorbed xylometazoline following topical (i.e., nasal) administration. The proposed mechanism may involve additive or synergistic increases in circulating catecholamines, such as noradrenaline (norepinephrine).', 'DDInter', 'Until further information is available, caution is advised if a tri- or tetra-cyclic antidepressant is used concomitantly with topical xylometazoline. Blood pressure and heart rate should be monitored at regular intervals during concomitant therapy. A dose reduction or discontinuation of concomitant therapy should be considered in patients who experience a sustained increase in blood pressure or heart rate.', 'Synergism', 'Until further information is available, caution is advised if a tri- or tetra-cyclic antidepressant is used concomitantly with topical xylometazoline.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/22784/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] Agencia Espaola de Medicamentos y Productos Sanitarios Healthcare "Centro de informacin online de medicamentos de la AEMPS - CIMA. Available from: URL: https://cima.aemps.es/cima/publico/home.html." ([2018]):', 'Citalopram, Sertraline, Paroxetine, Fluoxetine, St. John''s Wort, Nefazodone, Oxitriptan, Viloxazine, Vortioxetine, Trazodone, Tryptophan, More', NULL, 1767369485);
