INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (22908, 'Amlodipine', 'Duvelisib', 'Moderate', 'Coadministration with CYP450 3A4 inhibitors may increase the plasma concentrations of amlodipine, which is a substrate of the isoenzyme.', 'DDInter', 'Close monitoring of clinical response and tolerance is recommended if amlodipine is prescribed with potent or moderate CYP450 3A4 inhibitors. Dosage reduction may be required for amlodipine. Patients should be advised to seek medical attention if they experience edema or swelling of the lower extremities; sudden, unexplained weight gain; difficulty breathing; chest pain or tightness; or hypotension as indicated by dizziness, fainting, or orthostasis.', 'Metabolism', 'Close monitoring of clinical response and tolerance is recommended if amlodipine is prescribed with potent or moderate CYP450 3A4 inhibitors.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/22923/', '[1] Sasaki M, Maeda A, Fujimura A "Influence of diltiazem on the pharmacokinetics of amlodipine in elderly hypertensive patients." Eur J Clin Pharmacol 57 (2001): 85-6[2] Canadian Pharmacists Association "e-CPS. Available from: URL: http://www.pharmacists.ca/function/Subscriptions/ecps.cfm?link=eCPS_quikLink."[3] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[4] Cerner Multum, Inc. "Australian Product Information." O 0[5] "Product Information. Norvasc (amlodipine)." Pfizer US Pharmaceuticals, New York, NY.', 'Hydrochlorothiazide, Ramipril, Valsartan, Rosuvastatin, Lisinopril, Perindopril, Niacin, Pravastatin, Acetylsalicylic acid, Ezetimibe, Bempedoic acid, More', 'Dacomitinib, Pexidartinib, Vemurafenib, Vandetanib, Capmatinib, Ibrutinib, Afatinib, Tivozanib, Sunitinib, Regorafenib, Alpelisib, More', 176