INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (22911, 'Amlodipine', 'Efavirenz', 'Moderate', 'Coadministration with efavirenz may decrease the plasma concentrations of drugs that are substrates of the CYP450 3A4 isoenzyme. The mechanism is accelerated clearance due to induction of CYP450 3A4 activity by efavirenz.', 'DDInter', 'Caution is advised if efavirenz must be used concomitantly with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever efavirenz is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised if efavirenz must be used concomitantly with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/22926/', '[1] "Product Information. Sustiva (efavirenz)." DuPont Pharmaceuticals, Wilmington, DE.[2] "Product Information. Sustiva (efavirenz)." DuPont Pharmaceuticals, Wilmington, DE.', 'Aliskiren, Bempedoic acid, Bisoprolol, Propranolol, Nebivolol, Bisoprolol, Olmesartan, Azilsartan medoxomil, Nebivolol, Telmisartan, Candesartan, More', 'Paritaprevir, Glecaprevir, Tenofovir alafenamide, Cabotegravir, Zanamivir, Velpatasvir, Dolutegravir, Brincidofovir, Remdesivir, Elbasvir, Tipranavir, More', 1767369485);
