INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (24108, 'Amobarbital', 'Paricalcitol', 'Moderate', 'Coadministration with CYP450 inducers such as rifampin, isoniazid, barbiturates, and certain anticonvulsants may decrease the pharmacologic effects of vitamin D analogs. These agents are thought to induce the hepatic conversion of vitamin D to inactive metabolites and have been shown to reduce circulating levels of active vitamin D, sometimes accompanied by reduced serum calcium and increased parathyroid hormone levels. Patients on long-term anticonvulsant therapy have occasionally developed osteomalacia, presumably due to interference with vitamin D and calcium metabolism. There have also been isolated reports of patients who responded poorly to vitamin D supplements during treatment with phenytoin and/or primidone.', 'DDInter', 'Patients receiving vitamin D analogs with CYP450 inducers should be monitored for potentially reduced vitamin D effects. Dosage adjustments may be necessary.', 'Metabolism', 'Patients receiving vitamin D analogs with CYP450 inducers should be monitored for potentially reduced vitamin D effects.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/24127/', '[1] "Product Information. Rocaltrol (calcitriol)." Roche Laboratories, Nutley, NJ.[2] "Product Information. Zemplar (paricalcitol)." Abbott Pharmaceutical, Abbott Park, IL.[3] "Product Information. Hectorol (doxercalciferol)." Genzyme Corporation, Cambridge, MA.[4] "Product Information. One-Alpha (alfacalcidol)." Pharmel Inc, Montreal, IN.', 'Etelcalcetide, Cinacalcet, Salmon calcitonin', 'Suvorexant, Triazolam, Flurazepam, Methohexital, Zaleplon, Dichloralphenazone, Valerian, Chloral hydrate, Quazepam, Paraldehyde, Ramelteon, More', 1767369485);
