INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (24112, 'Amobarbital', 'Penbutolol', 'Moderate', 'Coadministration with barbiturates may decrease the plasma concentrations and pharmacologic effects of certain beta-blockers when administered orally. The proposed mechanism is barbiturate induction of hepatic microsomal and first-pass metabolism.', 'DDInter', 'Barbiturates may variously reduce the effects of certain beta-blockers when given for more than a few days. Pharmacologic response to beta-blockers should be monitored more closely whenever a barbiturate is added to or withdrawn from therapy, and the beta-blocker dosage adjusted as necessary. Renally excreted beta-blockers such as atenolol, carteolol, nadolol, or sotalol are not expected to interact.', 'Metabolism', 'Barbiturates may variously reduce the effects of certain beta-blockers when given for more than a few days.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/24131/', '[1] Mantyla R, Mannisto P, Nykanen S, Koponen A, Lamminsivu U "Pharmacokinetic interactions of timolol with vasodilating drugs, food and phenobarbitone in healthy human volunteers." Eur J Clin Pharmacol 24 (1983): 227-30[2] Seideman P, Borg K, Haglund K, Von Bahr C, "Decreased plasma concentrations and clinical effects of alprenolol during combined treatment with pentobarbitone in hypertension." Br J Clin Pharmacol 23 (1987): 267-71[3] Haglund K, Seideman P, Colliste P, Borg KO, von Bahr C "Influence of pentobarbital on metoprolol plasma levels." Clin Pharmacol Ther 26 (1979): 326-9[4] Sotaniemi EA, Anttila M, Pelkonen RO, Jarvensivu P, Sundquist H "Plasma clearance of propranolol and sotalol and hepatic drug-metabolizing enzyme activity." Clin Pharmacol Ther 26 (1979): 153-61[5] Branch RA, Her