INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (24610, 'Ampicillin', 'Chloramphenicol', 'Moderate', 'Chloramphenicol may inhibit the bactericidal effect of penicillins when used to treat bacterial meningitis and staphylococcal endocarditis. Chloramphenicol acts by inhibiting cellular protein synthesis and can change actively growing bacteria into static ones. Since penicillins work by inhibiting cell wall synthesis, their effectiveness may be reduced. In addition, neurologic toxicity may be enhanced.', 'DDInter', 'Until more information is available, this combination should probably be reserved for conditions where it has been found to be efficacious and avoided in situations where bactericidal activity is necessary, such as certain types of bacterial meningitis or staphylococcal endocarditis. If the combination is necessary, some experts recommend administering the penicillin several hours before the chloramphenicol. Patients should be closely monitored for altered antimicrobial efficacy.', 'Antagonism', 'Until more information is available, this combination should probably be reserved for conditions where it has been found to be efficacious and avoided in situations where bactericidal activity is necessary, such as certain types of bacterial meningitis or staphylococcal endocarditis.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/24629/', '[1] Shalit I, Marks MI "Chloramphenicol in the 1980s." Drugs 28 (1984): 281-91[2] Wallace JF, Smith RH, Garcia M, Petersdorf RG "Studies on the pathogenesis of meningitis. VI: antagonism between penicillin and chloramphenicol in experimental pneumococcal meningitis." J Lab Clin Med 70 (1967): 408-18[3] Jawetz E "The use of combinations of antimicrobial drugs." Annu Rev Pharmacol 8 (1