INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (24687, 'Anagrelide', 'Cilostazol', 'Moderate', 'Anagrelide is an inhibitor of cyclic AMP phosphodiesterase (PDE) III and may potentiate the pharmacologic effects of drugs with similar properties such as inotropes milrinone, enoximone, inamrinone, olprinone, and cilostazol. Anagrelide alone has been associated with cardiovascular side effects such as vasodilation, palpitations, tachycardia, congestive heart failure, myocardial infarction, cardiomyopathy, cardiomegaly, complete heart block, and atrial fibrillation.', 'DDInter', 'Caution is advised if anagrelide is coadministered with other inotropes or inhibitors. The risk of potentially additive cardiovascular adverse effects should be considered.', 'Synergism', 'Caution is advised if anagrelide is coadministered with other inotropes or inhibitors.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/24706/', '[1] "Product Information. Agrylin (anagrelide)." Roberts Pharmaceutical Corporation, Eatontown, NJ.', 'Pertuzumab, Olaparib, Tisagenlecleucel, Aminolevulinic acid, Tazemetostat, Altretamine, Tagraxofusp, Ixazomib, Sotorasib, Enasidenib, Belzutifan, More', 'Alteplase, Dipyridamole, Warfarin, Clopidogrel, Dicoumarol, Streptokinase, Antithrombin III human, Tenecteplase, Protein C, Acenocoumarol, Selexipag, More', 1767369485);
