INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (24738, 'Anagrelide', 'Epoprostenol', 'Moderate', 'Prostacyclin/prostacyclin analogs (e.g., epoprostenol, iloprost, treprostinil) inhibit platelet aggregation and may theoretically potentiate the risk of bleeding in patients treated with other agents that affect hemostasis such as anticoagulants, platelet inhibitors, thrombin inhibitors, thrombolytic agents, or agents that commonly cause thrombocytopenia.', 'DDInter', 'Caution is advised if prostacyclin and prostacyclin analogs are used in combination with other drugs that affect hemostasis. Close clinical and laboratory observation for hemorrhagic complications is recommended. Patients should be advised to promptly report any signs of bleeding to their physician, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, unusual bleeding or bruising, red or brown urine, or red or black stools.', 'Synergism', 'Caution is advised if prostacyclin and prostacyclin analogs are used in combination with other drugs that affect hemostasis.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/24757/', '[1] "Product Information. Ventavis (iloprost)." Actelion Pharmaceuticals US Inc, South San Francisco, CA.[2] "Product Information. Flolan (epoprostenol)." Glaxo Wellcome, Research Triangle Park, NC.[3] "Product Information. Remodulin (treprostinil)." United Therapeutics Corp, Silver Spring, MD.', 'Pertuzumab, Olaparib, Oxaliplatin, Rucaparib, Bortezomib, Tisagenlecleucel, Aminolevulinic acid, Tazemetostat, Altretamine, Tagraxofusp, Pegaspargase, More', 'Alteplase, Dipyridamole, Warfarin, Clopidogrel, Dicoumarol, Streptokinase, Antithrombin III human, Tenecteplase, Protein C, Acenocoumarol, Selexipag, More', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (24739, 'Anagrelide', 'Eptifibatide', 'Major', 'The combination of glycoprotein IIb/IIIa inhibitors and anagrelide can theoretically significantly increase the risk of bleeding. GP IIb/IIIa inhibitors inhibit platelet aggregation by blocking the fibrinogen, von Willebrand factor, and other adhesive ligands to platelets. The mechanism whereby anagrelide decreases platelet concentrations is not known. The combination of these drugs, therefore, could decrease platelet number and function.', 'DDInter', 'Extraordinary precautions are recommended if these drugs must be used together, including monitoring platelet counts and hemoglobin concentrations. In addition, invasive procedures, such as urinary catheterization, nasogastric or nasotracheal intubation, and even venous punctures should be avoided or minimized. Non-compressible venous access sites, such as jugular or subclavian sites, should be avoided.', 'Synergism', 'Extraordinary precautions are recommended if these drugs must be used together, including monitoring platelet counts and hemoglobin concentrations.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/24758/', '[1] "Product Information. Integrilin (eptifibatide)." Schering Laboratories, Kenilworth, NJ.[2] "Product Information. ReoPro (abciximab)." Lilly, Eli and Company, Indianapolis, IN.', 'Alteplase, Dipyridamole, Warfarin, Clopidogrel, Dicoumarol, Streptokinase, Antithrombin III human, Tenecteplase, Protein C, Acenocoumarol, Selexipag, More', 'Pertuzumab, Olaparib, Oxaliplatin, Rucaparib, Bortezomib, Tisagenlecleucel, Aminolevulinic acid, Tazemetostat, Altretamine, Tagraxofusp, Pegaspargase, More', 1767369485);
