INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (28002, 'Nelfinavir', 'Apixaban', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 that can also inhibit P-glycoprotein (P-gp) may significantly increase the plasma concentrations of apixaban, which is a substrate of both the isoenzyme and efflux transporter.', 'DDInter', 'The use of apixaban should either be avoided or the dosage reduced during concomitant systemic treatment with dual P-gp and potent CYP450 3A4 inhibitors. In addition, some authorities recommend avoiding concomitant use of apixaban during and for 2 weeks after treatment with itraconazole. For patients receiving apixaban 5 mg or 10 mg twice daily, the prescribing information recommends reducing the dosage by 50%. In patients already taking apixaban at a dosage of 2.5 mg twice daily (e,g., patients meeting at least 2 of the following criteria: age >=80 years; body weight <=60 kg; serum creatinine >=1.5 mg/dL), coadministration with dual P-gp and potent CYP450 3A4 inhibitors should be avoided.', 'Metabolism, Absorption', 'The use of apixaban should either be avoided or the dosage reduced during concomitant systemic treatment with dual P-gp and potent CYP450 3A4 inhibitors.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/28055/', '[1] "Product Information. Eliquis (apixaban)." Bristol-Myers Squibb Canada Inc, Montreal, IN.[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] "Product Information. Sporanox (itraconazole)." Janssen Pharmaceutica, Titusville, NJ.[4] Cerner Multum, Inc. "Australian Product Information." O 0[5] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with continuous enteral nutrition." Am J Health Syst Pharm 66 (2009): 1438-67[6] "Product Information. VFEND (voriconazole)." Pfizer U.S. Pharmaceuticals, New York, NY.', 'Lamivudine, Paritaprevir, Tenofovir alafenamide, Maraviroc, Simeprevir, Cabotegravir, Valaciclovir, Zanamivir, Rilpivirine, Dolutegravir, Oseltamivir, More', 'Clopidogrel, Iloprost, Lepirudin, Acenocoumarol, Urokinase, Cangrelor, Dipyridamole, Caplacizumab, Argatroban, Prasugrel, Desirudin, More', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (28003, 'Nepafenac (ophthalmic)', 'Apixaban', 'Moderate', 'Systemically and topically administered nonsteroidal anti-inflammatory drugs (NSAIDs) may potentiate the risk of bleeding in patients treated with anticoagulants and other drugs that affect hemostasis such as platelet inhibitors, thrombin inhibitors, thrombolytic agents, or agents that commonly cause thrombocytopenia. The pharmacologic effects of NSAIDs that contribute to this interaction include prolongation of prothrombin time and inhibition of platelet adhesion and aggregation.', 'DDInter', 'Caution is advised if NSAIDs are used in combination with other drugs that affect hemostasis. Close clinical and laboratory observation for hematologic complications is recommended. Patients should be advised to promptly report any signs of bleeding to their doctor, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, unusual bleeding or bruising, red or brown urine, or red or black stools.', 'Synergism', 'Caution is advised if NSAIDs are used in combination with other drugs that affect hemostasis.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/28056/', '[1] "Product Information. Nevanac (nepafenac ophthalmic)." Alcon Laboratories Inc, Fort Worth, TX.[2] "Product Information. Acular (ketorolac)." Allergan Inc, Irvine, CA.[3] "Product Information. Xibrom (bromfenac ophthalmic)." ISTA Pharmaceuticals, Irvine, CA.', 'Protein C, Acenocoumarol, Selexipag', NULL, 1767369485);
