INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (28076, 'Sunitinib', 'Apixaban', 'Moderate', 'The concomitant use of anticoagulants may theoretically increase the risk of sunitinib-induced bleeding. Severe and life-threatening tumor hemorrhage, hemoptysis, and pulmonary hemorrhage, and nonserious epistaxis have occurred during treatment with sunitinib.', 'DDInter', 'Monitoring for clinical and laboratory signs of bleeding is recommended during concomitant therapy, including INR/PT and complete blood counts. Patients should be advised to promptly report any potential signs of bleeding to their doctor, including nosebleeds, pain, swelling, or headache.', 'Synergism', 'Monitoring for clinical and laboratory signs of bleeding is recommended during concomitant therapy, including INR/PT and complete blood counts.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/28129/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] EMEA. European Medicines Agency "EPARs. European Union Public Assessment Reports. Available from: URL: http://www.ema.europa.eu/ema/index.jsp?curl=pages/includes/medicines/medicines_landingpage.jsp&mid."[3] "Product Information. Sutent (sunitinib)." Pfizer U.S. Pharmaceuticals Group, New York, NY.', 'Dacomitinib, Bosutinib, Vandetanib, Cobimetinib, Afatinib, Tivozanib, Axitinib, Ripretinib, Lenvatinib, Erlotinib, Osimertinib, More', 'Ticagrelor, Clopidogrel, Iloprost, Acenocoumarol, Urokinase, Cangrelor, Dipyridamole, Caplacizumab, Prasugrel, Streptokinase, Eptifibatide, More', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (28077, 'Suprofen (ophthalmic)', 'Apixaban', 'Moderate', 'Systemically and topically administered nonsteroidal anti-inflammatory drugs (NSAIDs) may potentiate the risk of bleeding in patients treated with anticoagulants and other drugs that affect hemostasis such as platelet inhibitors, thrombin inhibitors, thrombolytic agents, or agents that commonly cause thrombocytopenia. The pharmacologic effects of NSAIDs that contribute to this interaction include prolongation of prothrombin time and inhibition of platelet adhesion and aggregation.', 'DDInter', 'Caution is advised if NSAIDs are used in combination with other drugs that affect hemostasis. Close clinical and laboratory observation for hematologic complications is recommended. Patients should be advised to promptly report any signs of bleeding to their doctor, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, unusual bleeding or bruising, red or brown urine, or red or black stools.', 'Synergism', 'Caution is advised if NSAIDs are used in combination with other drugs that affect hemostasis.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/28130/', '[1] "Product Information. Nevanac (nepafenac ophthalmic)." Alcon Laboratories Inc, Fort Worth, TX.[2] "Product Information. Acular (ketorolac)." Allergan Inc, Irvine, CA.[3] "Product Information. Xibrom (bromfenac ophthalmic)." ISTA Pharmaceuticals, Irvine, CA.', 'Protein C, Acenocoumarol, Selexipag', NULL, 1767369485);
