INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (28817, 'Pitolisant', 'Apomorphine', 'Moderate', 'Apomorphine may cause modest prolongation of the QTc interval. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', 'DDInter', 'Caution is recommended if apomorphine is used in combination with other drugs that can prolong the QT interval. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope. If taking drugs that also cause CNS or orthostatic effects (e.g., psychotropic drugs like tricyclic antidepressants, phenothiazines, and neuroleptics), patients should be made aware of the possibility of additive effects with apomorphine and counseled to avoid activities requiring mental alertness until they know how these agents affect them.', 'Synergism', 'Caution is recommended if apomorphine is used in combination with other drugs that can prolong the QT interval.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/28870/', '[1] "Product Information. Apokyn (apomorphine)." Mylan Pharmaceuticals Inc, Morgantown, WV.[2] EMA. European Medicines Agency. European Union "EMA - List of medicines under additional monitoring. Available from: URL: http://www.ema.europa.eu/ema/index.jsp?curl=pages/regulation/document_listing/document_listing_000366.jsp&mid=WC0b01ac058067c852" ([2013 - ]):[3] Canadian Pharmacists Association "e-CPS. Available from: URL: http://www.pharmacists.ca/function/Subscriptions/ecps.cfm?link=eCPS_quikLink."[4] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[5] Cerner Multum, Inc. "Australian Product Information." O 0[6] "Product Information. Pletal (cilostazol)." Otsuka American Pharmaceuticals, Rockville, MD.', 'Fesoterodine, Pentosan polysulfate, Calcium chloride, Phenazopyridine, Sildenafil, Trospium, Mirabegron, Alprostadil, Acetohydroxamic acid, Yohimbine, Flavoxate, More', 'Dalfampridine, Riluzole, Inotersen, Amifampridine, Tafamidis', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (28818, 'Polyethylene glycol (3350)', 'Apomorphine', 'Moderate', 'Bowel cleansing as well as overuse of certain laxatives may cause electrolyte loss and increase the risk of torsade de pointes ventricular arrhythmia in patients treated with drugs that prolong the QT interval. Electrolyte disturbances including hypokalemia and hypomagnesemia have been reported with laxative abuse and are known risk factors for torsade de pointes associated with QT interval prolongation.', 'DDInter', 'Patients treated with drugs that prolong the QT interval should exercise caution when self-medicating with laxatives. The recommended dosage and duration of use should not be exceeded. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', 'Patients treated with drugs that prolong the QT interval should exercise caution when self-medicating with laxatives.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/28871/', '[1] Cerner Multum, Inc. "Australian Product Information." O 0[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] Schaefer DC, Cheskin LJ "Constipation in the elderly." Am Fam Physician 58 (1998): 907-14[4] Muller-Lissner SA "Adverse effects of laxatives: fact and fiction." Pharmacology 47 (1993): 138-45[5] Canadian Pharmacists Association "e-CPS. Available from: URL: http://www.pharmacists.ca/function/Subscriptions/ecps.cfm?link=eCPS_quikLink."[6] Chin RL "Laxative-induced hypokalemia." Ann Emerg Med 32 (1998): 517-8', 'Darifenacin, Tadalafil, Ammonium chloride, Yohimbine, Papaverine, Oxybutynin, Dimethyl sulfoxide, Pentosan polysulfate, Calcium chloride, Trospium, Flavoxate, More', NULL, 1767369485);
