INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (28924, 'Tripelennamine', 'Apomorphine', 'Moderate', 'Central nervous system (CNS) depressant effects may be additively or synergistically increased in patients using apomorphine in combination with other drugs that can also cause these effects. Apomorphine alone has been frequently associated with somnolence and dizziness. Patients may suddenly fall asleep during activities of daily living.', 'DDInter', 'The use of other sedating drugs should generally be avoided during apomorphine treatment. Patients prescribed these agents concurrently should be monitored for potentially excessive or prolonged CNS depression, especially if they are elderly or debilitated. Ambulatory patients should be made aware of the possibility of additive CNS effects (e.g., drowsiness, dizziness, lightheadedness, confusion) and counseled to avoid activities requiring mental alertness until they know how these agents affect them.', 'Synergism', 'The use of other sedating drugs should generally be avoided during apomorphine treatment.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/28977/', '[1] "Product Information. Apokyn (apomorphine)." Mylan Pharmaceuticals Inc, Morgantown, WV.[2] "Product Information. Zohydro ER (HYDROcodone)." Zogenix, Inc, San Diego, CA.', 'Pentosan polysulfate, Calcium chloride, Phenazopyridine, Sildenafil, Mirabegron, Alprostadil, Acetohydroxamic acid, Yohimbine, Avanafil, Papaverine, Vardenafil, More', 'Ketotifen, Levocetirizine, Cetirizine, Fexofenadine, Loratadine, Desloratadine, Azelastine, Pheniramine, Lidocaine, Tetracaine, Pheniramine', 1767369485);
