INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (35124, 'Atenolol', 'Licorice', 'Moderate', 'Licorice use has been associated with hypertension and may antagonize the effects of antihypertensive agents. Glycyrrhizic acid, a component of licorice, is hydrolyzed in the intestine to a metabolite (glycyrrhetinic acid) that causes mineralocorticoid and renin-suppressing effects.', 'DDInter', 'Patients receiving antihypertensive therapy should avoid or limit the consumption of licorice-containing products. Even relatively moderate doses of licorice may be problematic in susceptible patients when ingested regularly for prolonged periods.', 'Antagonism', 'Patients receiving antihypertensive therapy should avoid or limit the consumption of licorice-containing products.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/35180/', '[1] Cumming AM "Metabolic effects of licorice." Br Med J 1 (1977): 906[2] Epstein MT, Espiner EA, Donald RA, Hughes H "Effect of eating liquorice on the renin-angiotensin aldosterone axis in normal subjects." Br Med J 1 (1977): 488-90[3] Stewart PM, Wallace AM, Valentino R, Burt D, Shackleton CH, Edwards CR "Mineralocorticoid activity of liquorice: 11-beta-hydroxysteroid dehydrogenase deficiency comes of age." Lancet 2 (1987): 821-4[4] Elinav E, Chajek-Shaul T "Licorice consumption causing severe hypokalemic paralysis." Mayo Clin Proc 78 (2003): 767-8[5] Rosseel M, Schoors D "Chewing gum and hypokalaemia." Lancet 341 (1993): 175[6] Clyburn EB, DiPette DJ "Hypertension induced by drugs and other substances." Semin Nephrol 15 (1995): 72-86[7] Lin SH, Yang SS, Chau T, Halperin ML "An unusual cause of hypokalemic paralysis: chronic licorice ingestion." Am J Med Sci 325 (2003): 153-6[8] de Klerk GJ, Nieuwenhuis MG,