INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (35869, 'Telbivudine', 'Atorvastatin', 'Moderate', 'Prolonged use of certain nucleoside reverse transcriptase inhibitors may commonly cause myopathy, including rare cases of rhabdomyolysis. It is not known if the risk of myopathy is increased during concurrent administration of other agents associated with myopathy, including corticosteroids, fibric acid derivatives, HMG-CoA reductase inhibitors, chloroquine, hydroxychloroquine, penicillamine, and lipid-lowering doses of niacin.', 'DDInter', 'Clinicians should carefully consider the benefits versus risks prior to prescribing these agents together. Patients should be advised to promptly report any unexplained muscle pain, tenderness, or weakness, particularly if accompanied by malaise or fever.', 'Synergism', 'Clinicians should carefully consider the benefits versus risks prior to prescribing these agents together.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/35925/', '[1] "Product Information. Tyzeka (telbivudine)." IDEC Pharmaceuticals Corporation, San Diego, CA.[2] "Product Information. Retrovir (zidovudine)." Glaxo Wellcome, Research Triangle Park, NC.', 'Amlodipine, Ramipril, Valsartan, Lisinopril, Perindopril, Acetylsalicylic acid, Ezetimibe, Bempedoic acid, Indapamide, Mipomersen, Evinacumab, More', 'Tenofovir alafenamide, Cabotegravir, Maraviroc, Zanamivir, Rilpivirine, Dolutegravir, Brincidofovir, Elbasvir, Doravirine, Bictegravir, Pibrentasvir, More', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (35870, 'Telotristat ethyl', 'Atorvastatin', 'Moderate', 'Coadministration with telotristat ethyl may decrease the plasma concentrations of drugs that are substrates of CYP450 3A4 and/or 2B6 isoenzymes. The proposed mechanism is accelerated clearance due to induction of CYP450 3A4 and/or 2B6 (in vitro) isoenzymes by telotristat ethyl.', 'DDInter', 'When drugs that are known substrates of CYP450 3A4 and/or 2B6 are coadministered with telotristat ethyl, the possibility of a diminished therapeutic response to those drugs should be considered. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs, particularly those with a narrow therapeutic range, whenever telotristat ethyl is added to or withdrawn from therapy.', 'Metabolism', 'When drugs that are known substrates of CYP450 3A4 and/or 2B6 are coadministered with telotristat ethyl, the possibility of a diminished therapeutic response to those drugs should be considered.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/35926/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Xermelo (telotristat ethyl)." Lexicon Pharmaceuticals, Inc., The Woodlands, TX.[3] "Product Information. Xermelo (telotristat ethyl)." Lexicon Pharmaceuticals, Inc., The Woodlands, TX.', 'Ramipril, Valsartan, Rosuvastatin, Lisinopril, Perindopril, Niacin, Pravastatin, Acetylsalicylic acid, Ezetimibe, Bempedoic acid, Indapamide, More', NULL, 1767369485);
