INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (35900, 'Indinavir', 'Atovaquone', 'Moderate', 'Coadministration of atovaquone (750 mg twice a day with food) and indinavir (800 mg three times a day without food) for 14 days has been shown to decrease the trough plasma concentration (Cmin) of indinavir by 23%. The mechanism and clinical significance of this interaction are unknown. Steady-state indinavir peak plasma concentration (Cmax) and systemic exposure (AUC) were not altered by atovaquone.', 'DDInter', 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, caution is recommended in patients receiving indinavir in combination with atovaquone. Antiretroviral response should be closely monitored.', 'Others', 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, caution is recommended in patients receiving indinavir in combination with atovaquone.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/35956/', '[1] "Product Information. Malarone (atovaquone-proguanil)" Glaxo Wellcome, Research Triangle Pk, NC.[2] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with continuous enteral nutrition." Am J Health Syst Pharm 66 (2009): 1438-67[3] "Product Information. Malarone (atovaquone-proguanil)" Glaxo Wellcome, Research Triangle Pk, NC.[4] "Product Information. Mepron (atovaquone)." Glaxo Wellcome, Research Triangle Park, NC.[5] Yeh KC, Deutsch PJ, Haddix H, Hesney M, Hoagland V, Ju WD, Justice SJ, Osborne B, Sterrett AT, Stone JA, Woolf E, Waldman S "Single-dose pharmacokinetics of indinavir and the effect of food."