INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (37961, 'Entecavir', 'Aztreonam', 'Moderate', 'Coadministration of entecavir with another drug that is also eliminated by active tubular secretion may result in increased plasma concentrations of one or both drugs due to competitive inhibition of transporters in the renal tubules. Drugs (and/or their metabolites) that are thought to undergo active tubular secretion include acyclovir, allopurinol, aminosalicylic acid, cidofovir, cimetidine, creatine, dyphylline, famciclovir, famotidine, flecainide, ganciclovir, levetiracetam, metformin, methotrexate, midodrine, mycophenolic acid, oseltamivir, pralatrexate, probenecid, procainamide, quinidine, ranitidine, tenofovir, triamterene, trimethoprim, valacyclovir, valganciclovir, zalcitabine, zidovudine, and many of the beta-lactam and quinolone antibiotics.', 'DDInter', 'Patients receiving entecavir with another drug that undergoes active tubular secretion should be monitored for excessive pharmacologic effects of both drugs, and the dosages adjusted as necessary.', 'Excretion', 'Patients receiving entecavir with another drug that undergoes active tubular secretion should be monitored for excessive pharmacologic effects of both drugs, and the dosages adjusted as necessary.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/38017/', '[1] "Product Information. Baraclude (entecavir)." Bristol-Myers Squibb, Princeton, NJ.[2] "Product Information. Baraclude (entecavir)." Bristol-Myers Squibb, Princeton, NJ.', 'Cefepime, Ceftibuten, Ceftazidime, Cefiderocol, Ceftaroline fosamil, Ceftriaxone, Cefotetan', 'Lamivudine, Paritaprevir, Glecaprevir, Tenofovir alafenamide, Maraviroc, Amprenavir, Simeprevir, Valaciclovir, Zanamivir, Cabotegravir, Lenacapavir, More', 1767369485);
